(()=>{"use strict";(t=>{const e=document.getElementById("timer-days"),n=document.getElementById("timer-hours"),o=document.getElementById("timer-minutes"),m=document.getElementById("timer-seconds"),s=()=>{let t=(new Date("5 september 2022").getTime()-(new Date).getTime())/1e3;return{days:Math.floor(t/60/60/24),hours:Math.floor(t/60/60%24),minutes:Math.floor(t/60%60),seconds:Math.floor(t%60),timeRemaining:t}},x=setInterval((()=>{let t=s();t.timeRemaining>0?(e.textContent=t.days,n.textContent=t.hours,o.textContent=t.minutes,m.textContent=t.seconds):(n.textContent="00",o.textContent="00",m.textContent="00",clearInterval(x)),s(),1===e.textContent.length&&(e.textContent="0"+e.textContent),1===n.textContent.length&&(n.textContent="0"+n.textContent),1===o.textContent.length&&(o.textContent="0"+o.textContent),1===m.textContent.length&&(m.textContent="0"+m.textContent)}),1e3)})()})();